let lines = msg.payload.trim().split("\n");
let points = [];
let vehicle_id_from_topic = msg.topic.split("/")[1]; // Auto-ID aus Topic

lines.forEach(line => {
    let cols = line.split(",");
    let type = cols[0].trim().toLowerCase();

    if (type === "trip") {
        let [, trip_id, start_ts, end_ts, max_acc, max_brake, fuel_start, fuel_end, bat_start, bat_end] = cols;

        points.push({
            measurement: "trip_summary",
            tags: { vehicle_id: vehicle_id_from_topic, trip_id },
            fields: {
                max_acceleration_kmh: parseFloat(max_acc) || 0,
                max_braking_g: parseFloat(max_brake) || 0,
                fuel_start_l: parseFloat(fuel_start) || 0,
                fuel_end_l: parseFloat(fuel_end) || 0,
                battery_start_v: parseFloat(bat_start) || 0,
                battery_end_v: parseFloat(bat_end) || 0
            },
            timestamp: new Date(parseInt(start_ts) * 1000)
        });

    } else if (type === "state") {
        let [, ts, fuel, batt, state] = cols.slice(1); // Vehicle-ID aus Topic

        points.push({
            measurement: "vehicle_state",
            tags: { vehicle_id: vehicle_id_from_topic, state },
            fields: {
                fuel_l: parseFloat(fuel) || 0,
                battery_voltage_v: parseFloat(batt) || 0
            },
            timestamp: new Date(parseInt(ts) * 1000)
        });

    } else if (type === "error") {
        let [, ts, error_code, active] = cols.slice(1);

        points.push({
            measurement: "vehicle_errors",
            tags: { vehicle_id: vehicle_id_from_topic, error_code },
            fields: { active: parseInt(active) || 0 },
            timestamp: new Date(parseInt(ts) * 1000)
        });
    }
});

msg.payload = points; // immer Array
return msg;
